Events.CanAnalyzeItem:
(switch
	;	We can only handle items of levels 1-3

	(not (itmMatches (@ gData 'itemToAnalyze) "* L:1-3;"))
		{	canAnalyzeItem: Nil
			message: (cat "Unfortunately, the analyzer is not sophisticated enough to scan " (itmGetName (@ gData 'itemToAnalyze) 0x804) ".")
			}

	{	canAnalyzeItem: True }
	)

(switch
	;	We can only handle items of levels 1-6

	(not (itmMatches (@ gData 'itemToAnalyze) "* L:1-6;"))
		{	canAnalyzeItem: Nil
			message: (cat "Unfortunately, the analyzer is not sophisticated enough to scan " (itmGetName (@ gData 'itemToAnalyze) 0x804) ".")
			}

	{	canAnalyzeItem: True }
	)

(switch
	;	We can only handle items of levels 1-10

	(not (itmMatches (@ gData 'itemToAnalyze) "* L:1-10;"))
		{	canAnalyzeItem: Nil
			message: (cat "Unfortunately, the analyzer is not sophisticated enough to scan " (itmGetName (@ gData 'itemToAnalyze) 0x804) ".")
			}

	{	canAnalyzeItem: True }
	)

StaticData.Data:
(
	{	criteria:"a <=3"	enhancement:0x0105	}
	{	criteria:"a <=5"	enhancement:0x0103	}
	{	criteria:"a <=6"	enhancement:0x0101	}
	{	criteria:"a >6"	enhancement:Nil	descID:'TooAdvanced	}
	)

(
	{	criteria:"a <=6"	enhancement:Nil	descID:'TooPrimitive	}
	{	criteria:"a <=10"	enhancement:0x0105	}
	{	criteria:"a >10"	enhancement:Nil	descID:'TooAdvanced	}
	)

Events.OnUseOnItem:
(block (resultID)

	(if (shpIsRadioactive gPlayerShip)
		(block Nil
			(shpDecontaminate gPlayerShip)
			(setq resultID 'descResultDecontaminated)
			)
		(setq resultID 'descResultNotNeeded)
		)

	(objRemoveItem gPlayerShip gItem 1)
	(itmSetKnown gItem)
	{
		nextScreen: 'exitScreen
		desc: (itmTranslate gItem resultID)
		}
	)

(block (
	(dstItem (@ gData 'itemToUseOn))
	)
	(switch
		(not (itmMatches dstItem "aI"))
			Nil

		(block Nil
			(shpDamageArmor gPlayerShip dstItem 2 (rollDice 3 6 0))

			;	Remove the armor paste from the player's cargo
			(objRemoveItem gPlayerShip gItem 1)

			;	Identify the item
			(itmSetKnown gItem)

			{
				desc: (itmTranslate gItem 'descResult)
				}
			)
		)
	)

